def is_palindrome(s):
    # Base case: if the string is empty or has only one character, it is a palindrome
    if len(s) <= 1:
        return True

    # Recursive case: check if the first and last characters are equal
    if s[0] == s[-1]:
        # If they are equal, recursively check if the substring without the first and last characters is a palindrome
        return is_palindrome(s[1:-1])
    else:
        # If they are not equal, the string is not a palindrome
        return False

